import { useQuery } from "@apollo/client";
import { GET_ALL_ANIMES } from "../lib/queries/GetAllAnime";
import { useParams } from "react-router-dom";
import { CardContainer } from "../Components/card/Card";

export default function Home(props){
    const { param } = props.match   
    const pageId = parseInt(param) || 1;

    let {loading, error, data} = useQuery(GET_ALL_ANIMES, {
        variables: {
            perPage : 50,
            page : pageId,
        }
    })

    if(loading) return <div><h1>Loading...</h1></div>
    if(error) return <div><h1>Error : {error.message}</h1></div>

    return (
        <div 
        style = {{
            display: "grid",
            gridTemplateColumns: "1fr 1fr 1fr 1fr 1fr",
            backgroundColor: "#dedbd2" ,
            columnGap: "1rem",
            rowGap: "1rem",
        }}    
        >
        {parseInt(param)}
        {
            data.Page.media.map((media, index) => {
                return (
                    <CardContainer media={media}/>    
                )
            })
        }
        </div>
    );
}
