export function CardContainer({media}){

    return (
        <div
        style={{
            backgroundColor: "#b0c4b1",
            color: "#4a5759",
            padding: "10px",
            margin: "10px",
            display: "flex",
            flexDirection: "column",
            borderRadius: "10px",
            alignItems: "center",

        }}
        >   
        <Link></Link>
            <div 
            style={{
                alignContent: "center",
                // minHeight: "100px"
            }}
            >
                <CardImage src = {media.coverImage.medium} />
            </div>
            <div 
            style={{
                textAlign: "center",
            }}
            >
                <CardContent text={media.title.english}/>
            </div>
        </div>
    );
}

export function CardImage ({...Attr}){
    return <img {...Attr} alt="" />
}

export function CardContent({text}){
    return <h3>{text}</h3>
}
