export function CardContainer({media}){

    return (
        <div
        style={{
            backgroundColor: "#b0c4b1",
            color: "#4a5759",
            padding: "10px",
            margin: "10px",
            display: "flex",
            alignContent: "center",
            flexDirection: "column",
            borderRadius: "10px",
        }}
        >   
            <div 
            style={{
                width: "250px",
                height: "100px"
            }}
            >
                <CardImage src = {media.coverImage.large} />
            </div>
            <div 
            style={{
                textAlign: "center",
            }}
            >
                <CardContent text={media.title.english}/>
            </div>
        </div>
    );
}

export function CardImage ({...Attr}){
    return <img {...Attr} alt="" />
}

export function CardContent({text}){
    return <h3>{text}</h3>
}
