import { json } from "react-router-dom";
import { GET_ANIME_BY_IDS } from "../lib/queries/GetAnimeByIDS";
import { useQuery } from "@apollo/client";

export default function AnimeFav(){

    const favAnimeIDS = JSON.parse(localStorage.getItem("favAnime"));
    const { loading, error, data } = useQuery(GET_ANIME_BY_IDS, {
        variables: {
        ids: favAnimeIDS,
        },
    });

      return (
        <div 
        style = {{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            backgroundColor: "#dedbd2" ,
            // rowGap: "1rem",
        }}    
        >
        {
            data.Page.media.map((media, index) => {
                return (
                    <CardContaine media={media}/>    
                )
            })
        }
        </div>
      );
}
