import { useQuery } from "@apollo/client";
import { GET_ALL_ANIMES } from "../lib/queries/GetAllAnime";
import { useParams } from "react-router-dom";
import { CardContainer } from "../Components/card/Card";
import { useState } from "react";

export default function Search(){
    const { id } = useParams();
    const pageId = id ? parseInt(id) : 1;

    const { loading, error, data } = useQuery(GET_ALL_ANIMES, {
        variables: {
        perPage: 50,
        page: pageId,
        },
    });

    if(loading) return (
        <div style={{
            height: "90vh",
            display: "flex",
            textAlign: "center",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "large",
            backgroundColor: "#dedbd2",
            color: "#3a5a40"
        }}>
            Loading...
        </div>
        );
    if(error) return <div><h1>Error : {error.message}</h1></div>

    // console.log(data);

    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);

    handleSearch = (event) => {
        const searchTerm = event.target.value;
        setSearchTerm(searchTerm);
    }

    // perform search logic here, and update searchResults state accordingly
    const newSearchResults = performSearch(searchTerm);
    setSearchResults(newSearchResults);

    return (
        <div style = {{
            backgroundColor: "#dedbd2" ,
        }}
        >
            <input type="text" value={searchTerm} onChange={handleSearch} />

            <div 
            style = {{
                display: "grid",
                gridTemplateColumns: "1fr 1fr",
            }}    
            >
                
            {
                data.Page.media.map((media, index) => {
                    return (
                        <CardContainer media={media}/>    
                    )
                })
            }
            </div>
        </div>
    );
}
