import { gql } from "@apollo/client";

export const GET_ANIME_BY_IDS = gql`
query getAnimeByIds($ids: [Int]) {
    Media(id_in: $ids, type: ANIME) {
        id
        coverImage {
        medium
        }
        title {
        english
        }
        averageScore
    }
}
`
