import { useQuery } from "@apollo/client";
import { useParams } from "react-router-dom";
import { GET_ANIME_BY_ID } from "../lib/queries/GetAnimeByID";

export default function AnimeDetails(){
    const { id } = useParams();
    const animeId = id ? parseInt(id) : 1;

    
    const isFav = (id) => {
        let favAnime = localStorage.getItem("favAnime");
        if (favAnime !== null) {
            favAnime = JSON.parse(favAnime);
            return true;
        }
        return false;
    };

    const { loading, error, data } = useQuery(GET_ANIME_BY_ID, {
        variables: {
        id: animeId,
        },
    });

    const setFav = () => {
        let favAnime = localStorage.getItem("favAnime");
        if (favAnime === null) {
          favAnime = [];
        } else {
          favAnime = JSON.parse(favAnime);
        }
        if (!favAnime.includes(id)) {
          favAnime.push(id);
          localStorage.setItem("favAnime", JSON.stringify(favAnime));
        }
      }

    const removeFav = (idToRemove) => {
        let favAnime = localStorage.getItem("favAnime");
        if (favAnime !== null) {
            favAnime = JSON.parse(favAnime);
            favAnime = favAnime.filter(aid => aid !== idToRemove);
            localStorage.setItem("favAnime", JSON.stringify(favAnime));
        }
    };

    const handleFavClick = () => {
        let favAnime = JSON.parse(localStorage.getItem("favAnime")) || [];
        if (isFav) {
          // Remove anime from favorites
          favAnime = favAnime.filter((id) => id !== anime.id);
        } else {
          // Add anime to favorites
          favAnime.push(anime.id);
        }
        localStorage.setItem("favAnime", JSON.stringify(favAnime));
        setIsFav(!isFav); // Update the state
      };

    console.log(localStorage.getItem("favAnime"));

    if(loading) return <div><h1>Loading...</h1></div>
    if(error) return <div><h1>Error : {error.message}</h1></div>

    return (
        <div>
            <img src={data.Media.bannerImage} alt="" 
            style={{
                width: "100%",
            }}
            />
            <button onClick={handleFavClick}>
        {isFav ? "Remove from Favorites" : "Add to Favorites"}
      </button>
            
            
        </div>
    );
}
