import { GET_ANIME_BY_IDS } from "../lib/queries/GetAnimeByIDS";
import { useQuery } from "@apollo/client";
import { CardContainer } from "../Components/card/Card";

export default function AnimeFav(){

    let favAnimeIDs = JSON.parse(localStorage.getItem("favAnime"));
    let length;
    if (favAnimeIDs !== null) {
        favAnimeIDs = favAnimeIDs.map(id => parseInt(id, 10));
        length = favAnimeIDs.length;
    }

    let check = favAnimeIDs ? true : false;

    let { loading, error, data } = useQuery(GET_ANIME_BY_IDS, {
        variables: {
        ids: favAnimeIDs,
        perPage: length || 0,
        },
    });

    if(loading) return <div><h1>Loading...</h1></div>
    if(error) return(
        <div style={{
            height: "90vh",
            display: "flex",
            textAlign: "center",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "large",
            backgroundColor: "#dedbd2",
            color: "#3a5a40"
        }}>
            No favourite Anime
        </div>
    );
        


    let handleClear = () => {
        localStorage.clear();
    }
    

      return (
        <div>
        {
            check === true ? (
                <div>
                    <div style={{
                        display: "grid",
                        gridTemplateColumns: "1fr 1fr",
                        backgroundColor: "#dedbd2",
                    }}>
                        {data.Page.media.map((media, index) => {
                            return (
                                <CardContainer media={media} key={index}/>    
                            )
                        })}
                    </div>
                    
                    <button onClick={handleClear} style={{
                                border: "none", 
                                backgroundColor: "#FF3123",
                                borderRadius: "5px",
                    }}>
                        Clear all favourite Anime
                    </button>
                    
                </div>
            ) : (
                <div></div>
            )
          }
        </div>
      );
}
