import { GET_ANIME_BY_IDS } from "../lib/queries/GetAnimeByIDS";
import { useQuery } from "@apollo/client";
import { CardContainer } from "../Components/card/Card";

export default function AnimeFav(){

    let favAnimeIDs = JSON.parse(localStorage.getItem("favAnime")).map(id => parseInt(id, 10));
    let length = favAnimeIDs.length;
    let { loading, error, data } = useQuery(GET_ANIME_BY_IDS, {
        variables: {
        ids: favAnimeIDs,
        perPage: length,
        },
    });

    

    // console.log(favAnimeIDs);
    // console.log(data.Page.media);

      return (
        <div 
        style = {{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            backgroundColor: "#dedbd2" ,
            // rowGap: "1rem",
        }}    
        >
        {
            data.Page.media.map((media, index) => {
                return (
                    <CardContainer media={media}/>    
                )
            })
        }
        </div>
      );
}
